mapper.select:
SELECT DISTINCT t.* FROM st_menu t LEFT JOIN st_role_permission t1 on t1.PERMISSION_ID = t.ID WHERE t.STATUS in ('s', '1') AND t.TYPE in ('2')
AND t1.ROLE_ID = #{roleId}
ORDER BY sort

SELECT DISTINCT t.* FROM st_menu t LEFT JOIN st_role_permission t1 on t1.PERMISSION_ID = t.ID WHERE t.STATUS in ('s', '1') AND t.TYPE in ('3','4')
AND t1.ROLE_ID = #{roleId}
ORDER BY sort

SELECT DISTINCT t.* FROM st_menu t LEFT JOIN st_role_permission t1 on t1.PERMISSION_ID = t.ID WHERE t.STATUS in ('s', '1') AND t.TYPE in ('1','2') and t.pid is null
AND t1.ROLE_ID = #{roleId}
ORDER BY sort

SELECT DISTINCT t.* FROM st_menu t LEFT JOIN st_role_permission t1 on t1.PERMISSION_ID = t.ID WHERE t.STATUS in ('s', '1') AND t.TYPE in ('1','2')
and t.pid is null
and t.pid = #{pid}
AND t1.ROLE_ID = #{roleId}
ORDER BY sort
